import React, { useState, useEffect } from 'react';
import '../AnimeDetails/Profile.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import A1 from './A1'; // Import the A1 component

const ProfilePage = () => {
  const [name, setName] = useState('My Name');
  const [photoUrl, setPhotoUrl] = useState(null);
  const [isUpdated, setIsUpdated] = useState(false); // Track whether both name and photo are updated

  useEffect(() => {
    // Check if both name and photo are updated, then enable the Proceed button
    if (name !== 'My Name' && photoUrl) {
      setIsUpdated(true);
    } else {
      setIsUpdated(false);
    }
  }, [name, photoUrl]);

  const handleEditProfile = () => {
    const newName = prompt();

    if (newName) {
      setName(newName);
      toast('Your Profile name is successfully updated!');
    }
  };

  const handlePhotoUpload = (event) => {
    const file = event.target.files[0];

    if (file) {
      const photoObjectURL = URL.createObjectURL(file);
      toast.success('Profile photo updated');
      setPhotoUrl(photoObjectURL);
    }
  };

  const handleLogout = () => {
    // Implement logout logic here
  };

  const handleProceed = () => {
    toast.success('Welcome to ScreenPlayðŸ”¥'); // Display a success toast
    // Add any other actions you want to perform when the "Proceed" button is clicked.
  };

  return (
    <div>
      {/* ... other code ... */}
      <div className="profile-page">
        <div className="profile-header">
          <div className="profile-photo-container">
            <input
              type="file"
              accept="image/*"
              id="photo-upload-input"
              style={{ display: 'none' }}
              onChange={handlePhotoUpload}
            />
            <label htmlFor="photo-upload-input">
              {photoUrl ? (
                <img className="profile-photo" src={photoUrl} alt="" />
              ) : (
                <div className="profile-placeholder">Add Photo</div>
              )}
            </label>
          </div>
          <h2 className="profile-name">{name}</h2>
        </div>
        {/* ... other code ... */}
        {isUpdated && (
          <button className="edit-button" style={{ backgroundColor: 'red' }}>
            <a
              href="Home"
              style={{ textDecoration: 'none', color: '#fff' }}
              onClick={handleProceed}
            >
              Proceed
            </a>
          </button>
        )}
        {/* Pass name and photoUrl as props to A1 component */}
        <A1 name={name} photoUrl={photoUrl} />
        <ToastContainer />
      </div>
    </div>
  );
};

export default ProfilePage;
