import React, { useState } from 'react'
import { createUserWithEmailAndPassword, getAuth, fetchSignInMethodsForEmail } from 'firebase/auth';
import { app } from '../firebase';
import { getFirestore, collection, doc, setDoc } from 'firebase/firestore';
import { initializeApp } from 'firebase/app';

import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const auth = getAuth(app);

// Initialize Firestore
const firestore = getFirestore(app);


const Signup = () => {
  const [email, setEmail] = useState("");
  const [name, setname] = useState("");
  const [password, setpassword] = useState("");
  const [error, setError] = useState('');


  const createUser = async () => {
    try {
      // Check if the email is already in use
      const methods = await fetchSignInMethodsForEmail(auth, email);
      if (methods && methods.length > 0) {
        toast.error('Email address is already in use.');
        return;
      }
  
      // Create a new user
      await createUserWithEmailAndPassword(auth, email, password);
  
      // After successfully creating the user, store user data in Firestore
      const user = auth.currentUser;
      const userRef = firestore.collection('CreateUser').doc(user.uid);
      await userRef.set({
        email: user.email,
        // Add other user data as needed
      });
  
      toast.success('Registration successfully done.');
  
      // Use a setTimeout to delay the redirection
      setTimeout(() => {
        window.location.href = '/'; // Redirect to the desired URL (e.g., login page)
      }, 3000);
    } catch (error) {
      if (error.code === 'auth/email-already-in-use') {
        toast.error('Email address is already in use. Please login.');
      } else if (error.code === 'auth/weak-password') {
        toast.error('Password is too weak. Please use a stronger password.');
      } else {
        toast.error('Oops, something went wrong!');
      }
    }
  };
  


  return (

    <div>
      <header className="header">
        <div className="container">
          <div className="row">
            <div className="col-lg-2">
              <div className="header__logo">
                <a href="./index.html">
                  <img src="img/logo2.png" alt="ScreenPlay" style={{ width: 100, height: 50, position: 'absolute', top: 8, left: 3 }} />
                </a>
              </div>
            </div>
            <div className="col-lg-8">
              <div className="header__nav">
              </div>
            </div>
            <div className="col-lg-2">
            </div>
          </div>
        </div>
      </header>
      <section className="normal-breadcrumb set-bg" data-setbg="img/normal-breadcrumb.jpg">
      <img src='img/hero/hero-2.jpg' alt style={{ width: 1500, height: 500 }} />
        <div className="container">
          <div className="row">
            <div className="col-lg-12 text-center">
              <div className="normal__breadcrumb__text">
                <h2>Sign Up</h2>
                <p>Welcome to the official ScreenPlay.</p>
              </div>
            </div>
          </div>
        </div>
      </section>
      <section className="signup spad">
        <div className="container">
          <div className="row">
            <div className="col-lg-6">
              <div className="login__form">
                <h3>Sign Up</h3>
                <form onSubmit={(e) => {
                  e.preventDefault(); // Prevent the default form submission
                  createUser(); // Call your createUser function
                }}>
                  <div className="input__item">
                    <input type="email" placeholder="Email address"
                      onChange={(e) => setEmail(e.target.value)}
                      value={email}
                      required />
                    <span className="icon_mail" />
                  </div>
                  <div className="input__item">
                    <input type="text" placeholder="Your Name"
                      onChange={(e) => setname(e.target.value)}
                      value={name}
                      required />
                    <span className="icon_profile" />
                  </div>
                  <div className="input__item">
                    <input type="Password" placeholder="Password"
                      onChange={(e) => setpassword(e.target.value)}
                      value={password}
                      required />
                    <span className="icon_lock" />
                  </div>
                  <h5 style={{ color: 'red' }}>Signup implies agreement to ScreenPlay's T&C!</h5>
                  <br></br>
                  <button type="submit" className="site-btn">Sign Up</button>
                </form>
                <h5>Already have an account? <a href="/" style={{ color: 'blue' }}>Log In!</a></h5>
              </div>
            </div>
            <div className="col-lg-6">
              <div className="login__social__links">
                <h3>Signup With:</h3>
                <ul>
                  <li><a href="#" className="facebook"><i className="fa fa-facebook" /> Sign in With Facebook</a>
                  </li>
                  <li><a href="#" className="google"><i className="fa fa-google" /> Sign in With Google</a></li>
                  <li><a href="#" className="twitter"><i className="fa fa-twitter" /> Sign in With Twitter</a></li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </section>
      <footer className="footer">
        <div className="page-up">
          <a href="#" id="scrollToTopButton"><span className="arrow_carrot-up" /></a>
        </div>
        <div className="container">
          <div className="row">
            <div className="col-lg-3">
              <div className="footer__logo">
