import React, { useState } from 'react'
import {  signInWithEmailAndPassword, getAuth, fetchSignInMethodsForEmail } from 'firebase/auth';
import { app } from '../firebase';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
const auth = getAuth(app);
const Login = () => {

  const [email, setEmail] = useState("");
  const [password, setpassword] = useState("");
  const [error, setError] = useState('');

  const loginUser = async (email, password) => {
    try {
      // Attempt the login
      await signInWithEmailAndPassword(auth, email, password);
  
      // Display a success toast notification for a successful login
      toast.success('Login successful');
  
      // Use a setTimeout to delay the redirection after the toast notification
      setTimeout(() => {
        window.location.href = 'profile'; // Redirect to the dashboard or another page
      }, 3000); // Redirect after a 3-second delay (adjust the time as needed)
    } catch (error) {
      if (error.code === 'auth/user-not-found') {
        toast.error('User not found. Please check your email and password.');
      } else if (error.code === 'auth/wrong-password') {
        toast.error('Incorrect password. Please try again.');
      } else {
        toast.error('Login failed. Please check your credentials.');
      }
    }
  };
  
  return (
  <div>
   <header className="header">
    <div className="container">
      <div className="row">
        <div className="col-lg-2">
          <div className="header__logo">
            <a href="Login">
              <img src="img/logo2.png" alt="ScreenPlay" style={{width: 100, height: 50, position: 'absolute', top: 8, left: 3}} />
            </a>
          </div>
        </div>
        <div className="col-lg-8">
          <div className="header__nav">
          </div>
        </div>
        <div className="col-lg-2">
          <div className="header__right">
          </div>
        </div>
      </div>
    </div>
  </header>
  {/* Header End */}
  {/* Normal Breadcrumb Begin */}
  <section className="normal-breadcrumb set-bg" data-setbg="img/normal-breadcrumb.jpg">
    <div className="container">
      <div className="row">
        <div className="col-lg-12 text-center">
          <div className="normal__breadcrumb__text">
            <h2>Login</h2>
            <p>Welcome to the official ScreenPlay.</p>
          </div>
        </div>
      </div>
    </div>
  </section>
  {/* Normal Breadcrumb End */}
  {/* Login Section Begin */}
  <section className="login spad">
    <div className="container">
      <div className="row">
        <div className="col-lg-6">
          <div className="login__form">
            <h3>Login</h3>
            <form onSubmit={(e) => {
                  e.preventDefault(); // Prevent the default form submission
                  loginUser(); // Call your createUser function
                }}>
              <div className="input__item">
                <input type="text" placeholder="Email address" 
                 onChange={(e) => setEmail(e.target.value)}
                 value={email}
                 required/>
                <span className="icon_mail" />
              </div>
              <div className="input__item">
                <input type="text" placeholder="Password" />
                <span className="icon_lock" />
              </div>
              <button type="submit" className="site-btn" >
                <a href="Profile" style={{ textDecoration: 'none', color: '#fff', }}>
                Login Now 
                </a>
              </button>
            </form>
            <a href="ForgotPassword" className="forget_pass">Forgot Your Password?</a>
          </div>
        </div>
        <div className="col-lg-6">
          <div className="login__register">
            <h3>Dont’t Have An Account?</h3>
            <a href="Signup" className="primary-btn">Register Now</a>
          </div>
        </div>
      </div>
    </div>
    </section>
  {/* Login Section End */}
  {/* Footer Section Begin */}
  <footer className="footer">
    <div className="page-up">
      <a href="#" id="scrollToTopButton"><span className="arrow_carrot-up" /></a>
    </div>
    <div className="container">
      <div className="row">
        <div className="col-lg-3">
          <div className="footer__logo">
            <a href="./index.html"><img src="img/logo2.png" alt style={{width: 100, height: 50}} /></a>
          </div>
        </div>
        <div className="col-lg-6">
          <div className="footer__nav">
          <p>{/* Link back to Colorlib can't be removed. Template is licensed under CC BY 3.0. */}
            Copyright © All rights reserved | <i className="fa fa-heart" aria-hidden="true" /> by <a href="https://miraic.com/" target="_blank">Miraic Inc</a>
            {/* Link back to Colorlib can't be removed. Template is licensed under CC BY 3.0. */}</p>
          </div>
        </div>
        <div className="col-lg-3">
         
        </div>
      </div>
    </div>
  </footer>
  {/* Footer Section End */}
  {/* Search model Begin */}
  <div className="search-model">
    <div className="h-100 d-flex align-items-center justify-content-center">
      <div className="search-close-switch"><i className="icon_close" /></div>
      <form className="search-model-form">
        <input type="text" id="search-input" placeholder="Search here....." />
      </form>
    </div>
  </div>
  {/* Search model end */}
  {/* Js Plugins */}
</div>

  )
}

export default Login
