import React, { useState, useEffect } from 'react';
import '../AnimeDetails/Profile.css'; 
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ProfilePage = () => {
  const [userInfo, setUserInfo] = useState({
    name: 'My Name',
    photoUrl: null,
  });

  const [isUpdated, setIsUpdated] = useState(false); // Track whether both name and photo are updated

  useEffect(() => {
    // Check if both name and photo are updated, then enable the Proceed button
    if (userInfo.name !== 'My Name' && userInfo.photoUrl) {
      setIsUpdated(true);
    } else {
      setIsUpdated(false);
    }
  }, [userInfo]);

  const handleEditProfile = () => {
    const newName = prompt();

    if (newName) {
      setUserInfo((prevUserInfo) => ({ ...prevUserInfo, name: newName }));
      toast('Your Profile name is successfully updated!');
    }
  };

  const handlePhotoUpload = (event) => {
    const file = event.target.files[0];

    if (file) {
      const photoObjectURL = URL.createObjectURL(file);
      toast.success('Profile photo updated');
      setUserInfo((prevUserInfo) => ({ ...prevUserInfo, photoUrl: photoObjectURL }));
    }
  };

  const handleLogout = () => {
    // Implement logout logic here
  };

  const handleProceed = () => {
    toast.success('Welcome to ScreenPlayðŸ”¥'); // Display a success toast
    // Add any other actions you want to perform when the "Proceed" button is clicked.
  };

  return (
    <div>
      {/* ... Your header and other JSX code ... */}

      <div className="profile-page">
        <div className="profile-header">
          <div className="profile-photo-container">
            <input
              type="file"
              accept="image/*"
              id="photo-upload-input"
              style={{ display: 'none' }}
              onChange={handlePhotoUpload}
            />
            <label htmlFor="photo-upload-input">
              {userInfo.photoUrl ? (
                <img className="profile-photo" src={userInfo.photoUrl} alt="" />
              ) : (
                <div className="profile-placeholder">Add Photo</div>
              )}
            </label>
          </div>
          <h2 className="profile-name">{userInfo.name}</h2>
        </div>
        <div className="profile-actions">
          <button className="edit-button" onClick={handleEditProfile}>
            Edit Name
          </button>
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
          {isUpdated && (
            <button className="edit-button" style={{ backgroundColor: 'red' }}>
              <a href="Home" style={{ textDecoration: 'none', color: '#fff' }} onClick={handleProceed}>
                Proceed
              </a>
            </button>
          )}
          <ToastContainer />
        </div>
      </div>
    </div>
  );
};

export default ProfilePage;
