import React, { useState, useEffect } from 'react';
import '../AnimeDetails/Profile.css'; 
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ProfilePage = () => {
  const [name, setName] = useState('Your Name');
  const [photoUrl, setPhotoUrl] = useState(null);

  const handleEditProfile = () => {
    const newName = prompt();

    if (newName) {
      setName(newName);
      toast('Your Prifile name is sucessfully updated!')
    }
  };

  const handlePhotoUpload = (event) => {
    const file = event.target.files[0];

    if (file) {
      // You can upload the file to your server or do whatever you want with it here.
      // For this example, we'll just set the photoUrl to a local URL.
      const photoObjectURL = URL.createObjectURL(file);
      toast.success('Profile photo updated')
      setPhotoUrl(photoObjectURL);
    }
  };

  const handleLogout = () => {
    // Implement logout logic here
  };


  const [showDropdown, setShowDropdown] = useState(true);

  const handleMenuToggle = () => {
    setShowDropdown(!showDropdown);
  };
  
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth >= 1280 && window.innerHeight >= 1900) {
        setShowDropdown(true);
      } else {
        setShowDropdown(false);
      }
    };
  
    window.addEventListener('resize', handleResize);
    handleResize();
  
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);


  return (
    <div>
      <header className="header">
    <div className="container">
        <div className="row">
          <div className="col-lg-2">
            <div className="header__logo">
              <a href="Home">
                <img src="img/logo2.png" alt="ScreenPlay" style={{ width: 100, height: 50 }} />
              </a>
            </div>
          </div>
          <div className="col-lg-8">
            <div className="header__nav">
              <nav className="header__menu mobile-menu">
                <ul>
                  <li className="active"><a href="Home">Homepage</a></li>
                  <li><a href="Gallery">Gallery</a></li>
                  <li><a href="Contactus">Contact</a></li>
                  <a href="Search" className="sar"><span className="icon_search" /></a>
                  <a href="Profile"  className="pro"><span className="icon_profile" /></a>
                </ul>
              </nav>
            </div>
          </div>
          <div className="col-lg-2">
            <div className="header__right">
              <div className={`menu-button${showDropdown ? ' active' : ''}`} onClick={handleMenuToggle}>
                <span></span>
                <span></span>
                <span></span>
              </div>
              {showDropdown && (
                <ul className="dropdown-menu">
                  <li><a href="Home">Home</a></li>
                  <li><a href="Gallery">Gallery</a></li>
                  <li><a href="Contactus">Contacts Us</a></li>
                  <li><a href="Search" className="search-switch"><span className="icon_search" /></a></li>
                  <li><a href="Profile"><span className="icon_profile"/></a></li>
                </ul>
              )}
            </div>
          </div>
        </div>
      </div>
      </header>
    <div className="profile-page">
      <div className="profile-header">
        <div className="profile-photo-container">
          <input
            type="file"
            accept="image/*"
            id="photo-upload-input"
            style={{ display: 'none' }}
            onChange={handlePhotoUpload}
          />
          <label htmlFor="photo-upload-input">
            {photoUrl ? (
              <img className="profile-photo" src={photoUrl} alt="" />
            ) : (
              <div className="profile-placeholder">Add Photo</div>
            )}
          </label>
        </div>
        <h2 className="profile-name">{name}</h2>
      </div>
      <div className="profile-actions">
        <button className="edit-button" onClick={handleEditProfile}>
          Edit Profile
        </button>
        <button className="logout-button" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </div>
    </div>
  );
};

export default ProfilePage;
