import React, { useState } from 'react';
import { createUserWithEmailAndPassword, getAuth, fetchSignInMethodsForEmail } from 'firebase/auth';
import { app } from '../firebase';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const auth = getAuth(app);

const Signup = () => {
  const [email, setEmail] = useState('');
  const [name, setName] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSignup = () => {
    // Check if the email is already in use (This is just a basic check)
    const users = JSON.parse(localStorage.getItem('users')) || [];
    const existingUser = users.find((user) => user.email === email);
    if (existingUser) {
      setError('Email address is already in use.');
      toast.error("Email address is already in use.");
      return;
    }

    // Create a new user object
    const newUser = {
      name,
      email,
      password,
    };

    // Save the user in local storage
    users.push(newUser);
    localStorage.setItem('users', JSON.stringify(users));

    // Show a success message
    setError('Registration successfully done.');
    toast.success("Registration successfully done.");

    // Clear the input fields
    setEmail('');
    setName('');
    setPassword('');
  };


  return (

    <div>
      <header className="header">
        <div className="container">
          <div className="row">
            <div className="col-lg-2">
              <div className="header__logo">
                <a href="./index.html">
                  <img src="img/logo2.png" alt="ScreenPlay" style={{ width: 100, height: 50, position: 'absolute', top: 8, left: 3 }} />
                </a>
              </div>
            </div>
            <div className="col-lg-8">
              <div className="header__nav">
              </div>
            </div>
            <div className="col-lg-2">
            </div>
          </div>
        </div>
      </header>
      <br></br>
      <section className="normal-breadcrumb set-bg" data-setbg="img/normal-breadcrumb.jpg">
      <img
  src="img/Sbg3.jpg"
  alt=""
  style={{
    width: '1800px', // Adjust the width as needed
    height: '305px', // Adjust the height as needed
    position: 'fixed',
    top: '35%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
  }}
/>

        <div className="container">
          <div className="row">
            <div className="col-lg-12 text-center">
              <div className="normal__breadcrumb__text">
                <h2 style={{ color: '#FFF', fontWeight: 'bold' }}>Sign Up</h2>
                <p style={{ color: '#FFF', fontWeight: 'bold' }}>Welcome to the official ScreenPlay.</p>
              </div>
            </div>
          </div>
        </div>
      </section>
      <section className="signup spad">
        <div className="container">
          <div className="row">
            <div className="col-lg-6">
              <div className="login__form">
                <h3>Sign Up</h3>
                <form onSubmit={(e) => {
                  e.preventDefault(); // Prevent the default form submission
                  handleSignup(); // Call your createUser function
                }}>
                  <div className="input__item">
                    <input type="email" placeholder="Email address"
                      onChange={(e) => setEmail(e.target.value)}
                      value={email}
                      required />
                    <span className="icon_mail" />
                  </div>
                  <div className="input__item">
                    <input type="text" placeholder="Your Name"
                      onChange={(e) => setName(e.target.value)}
                      value={name}
                      required />
                    <span className="icon_profile" />
                  </div>
                  <div className="input__item">
                    <input type="Password" placeholder="Password"
                      onChange={(e) => setPassword(e.target.value)}
                      value={password}
                      required />
                    <span className="icon_lock" />
                  </div>
                  <h5 style={{ color: 'red' }}>Signup implies agreement to ScreenPlay's T&C!</h5>
                  <br></br>
                  <button type="submit" className="site-btn">Sign Up</button>
                </form>
                <h5>Already have an account? <a href="/" style={{ color: 'blue' }}>Log In!</a></h5>
              </div>
            </div>
            <div className="col-lg-6">
              <div className="login__social__links">
                <h3>Signup With:</h3>
                <ul>
                  <li><a href="#" className="facebook"><i className="fa fa-facebook" /> Sign in With Facebook</a>
                  </li>
                  <li><a href="#" className="google"><i className="fa fa-google" /> Sign in With Google</a></li>
                  <li><a href="#" className="twitter"><i className="fa fa-twitter" /> Sign in With Twitter</a></li>
                </ul>
              </div>
            </div>
          </div>
        </div>
      </section>
      <footer className="footer">
        <div className="page-up">
          <a href="#" id="scrollToTopButton"><span className="arrow_carrot-up" /></a>
        </div>
        <div className="container">
          <div className="row">
            <div className="col-lg-3">
              <div className="footer__logo">
                <a href="./index.html"><img src="img/logo2.png" alt style={{ width: 100, height: 50 }} /></a>
              </div>
            </div>
            <div className="col-lg-6">
              <div className="footer__nav">
                <p>{/* Link back to Colorlib can't be removed. Template is licensed under CC BY 3.0. */}
                  Copyright Â© All rights reserved | <i className="fa fa-heart" aria-hidden="true" /> by <a href="https://miraic.com/" target="_blank">Miraic Inc</a>
                  {/* Link back to Colorlib can't be removed. Template is licensed under CC BY 3.0. */}</p>
              </div>
            </div>
            <div className="col-lg-3">

            </div>
          </div>
        </div>
      </footer>
      <div className="search-model">
        <div className="h-100 d-flex align-items-center justify-content-center">
          <div className="search-close-switch"><i className="icon_close" /></div>
          <form className="search-model-form">
            <input type="text" id="search-input" placeholder="Search here....." />
          </form>
        </div>
      </div>
    </div>
  )
}

export default Signup
