import React, { useState } from 'react';
import { getAuth, signInWithEmailAndPassword } from 'firebase/auth';
import { app } from '../firebase';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const auth = getAuth(app);

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  // Function to handle the login
  const handleLogin = () => {
    // Get the list of registered users from local storage
    const users = JSON.parse(localStorage.getItem('users')) || [];

    // Find the user by email
    const user = users.find((u) => u.email === email);

    // Check if the user exists and the password matches
    if (user && user.password === password) {
      toast.success('Login successful');
      // Login successful
      setError(''); // Clear any previous errors
      // Redirect to a profile page or any desired page
      // Replace '/Profile' with the desired URL
      window.location.href = 'Profile';
    } else {
      // Login failed
      toast.error('Login failed please check your username and password and try again.');
      setError('Login failed. Please try again.');
    }
  };

  return (
  <div>
   <header className="header">
    <div className="container">
      <div className="row">
        <div className="col-lg-2">
          <div className="header__logo">
            <a href="Login">
              <img src="img/logo2.png" alt="ScreenPlay" style={{width: 100, height: 50, position: 'absolute', top: 8, left: 3}} />
            </a>
          </div>
        </div>
        <div className="col-lg-8">
          <div className="header__nav">
          </div>
        </div>
        <div className="col-lg-2">
          <div className="header__right">
          </div>
        </div>
      </div>
    </div>
  </header>
  {/* Header End */}
  {/* Normal Breadcrumb Begin */}
  <section className="normal-breadcrumb set-bg" data-setbg="img/normal-breadcrumb.jpg">
  <img
  src="img/loginbg.jpg"
  alt=""
  style={{
    width: '1800px', // Adjust the width as needed
    height: '305px', // Adjust the height as needed
    position: 'fixed',
    top: '35%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
  }}
/>
    <div className="container">
      <div className="row">
        <div className="col-lg-12 text-center">
          <div className="normal__breadcrumb__text">
            <h2 style={{ color: '#FFFFFF' }}>Login</h2>
            <p style={{ color: '#FFFFFF' }}>Welcome to the official ScreenPlay.</p>
          </div>
        </div>
      </div>
    </div>
  </section>
  {/* Normal Breadcrumb End */}
  {/* Login Section Begin */}
  <section className="login spad">
    <div className="container">
      <div className="row">
        <div className="col-lg-6">
          <div className="login__form">
          <div className="login__register">
            <a href="Signup" className="primary-btn">Register Now</a>
        </div>
          </div>
        </div>
      </div>
    </div>
    </section>
  {/* Login Section End */}
  {/* Footer Section Begin */}
  <footer className="footer">
    <div className="page-up">
      <a href="#" id="scrollToTopButton"><span className="arrow_carrot-up" /></a>
    </div>
    <div className="container">
      <div className="row">
        <div className="col-lg-3">
          <div className="footer__logo">
            <a href="./index.html"><img src="img/logo2.png" alt style={{width: 100, height: 50}} /></a>
          </div>
        </div>
        <div className="col-lg-6">
          <div className="footer__nav">
          <p>{/* Link back to Colorlib can't be removed. Template is licensed under CC BY 3.0. */}
            Copyright © All rights reserved | <i className="fa fa-heart" aria-hidden="true" /> by <a href="https://miraic.com/" target="_blank">Miraic Inc</a>
            {/* Link back to Colorlib can't be removed. Template is licensed under CC BY 3.0. */}</p>
          </div>
        </div>
        <div className="col-lg-3">
        </div>
      </div>
    </div>
  </footer>
  {/* Footer Section End */}
  {/* Search model Begin */}
  <div className="search-model">
    <div className="h-100 d-flex align-items-center justify-content-center">
      <div className="search-close-switch"><i className="icon_close" /></div>
      <form className="search-model-form">
        <input type="text" id="search-input" placeholder="Search here....." />
      </form>
    </div>
  </div>
  {/* Search model end */}
  {/* Js Plugins */}
</div>

  )
}

export default Login
