import React, { useState, useEffect } from 'react';
import '../AnimeDetails/Profile.css'; 
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ProfilePage = () => {
  const [name, setName] = useState('My Name');
  const [photoUrl, setPhotoUrl] = useState(null);
  const [isUpdated, setIsUpdated] = useState(false); // Track whether both name and photo are updated

  useEffect(() => {
    // Check if both name and photo are updated, then enable the Proceed button
    if (name !== 'My Name' && photoUrl) {
      setIsUpdated(true);
    } else {
      setIsUpdated(false);
    }
  }, [name, photoUrl]);

  const handleEditProfile = () => {
    const newName = prompt();

    if (newName) {
      setName(newName);
      toast('Your Profile name is successfully updated!')
    }
  };

  const handlePhotoUpload = (event) => {
    const file = event.target.files[0];

    if (file) {
      const photoObjectURL = URL.createObjectURL(file);
      toast.success('Profile photo updated')
      setPhotoUrl(photoObjectURL);
    }
  };

  const handleLogout = () => {
    // Implement logout logic here
  };

  return (
    <div>
    <div>
      <header className="header">
        <div className="container">
          <div className="row">
            <div className="col-lg-2">
              <div className="header__logo">
                <a href="Home">
                  <img src="img/logo2.png" alt="ScreenPlay" style={{ width: 100, height: 50 }} />
                </a>
              </div>
            </div>
          </div>
        </div>
      </header>
      <div className="profile-page">
        <div className="profile-header">
          <div className="profile-photo-container">
            <input
              type="file"
              accept="image/*"
              id="photo-upload-input"
              style={{ display: 'none' }}
              onChange={handlePhotoUpload}
            />
            <label htmlFor="photo-upload-input">
              {photoUrl ? (
                <img className="profile-photo" src={photoUrl} alt="" />
              ) : (
                <div className="profile-placeholder">Add Photo</div>
              )}
            </label>
          </div>
          <h2 className="profile-name">{name}</h2>
        </div>
        <div className="profile-actions">
          <button className="edit-button" onClick={handleEditProfile}>
            Edit Name
          </button>
          <button className="logout-button" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>
    </div>
      {isUpdated && (
        <button className="edit-button" style={{ marginTop: '150px' }}>
        <a href="Home" style={{ textDecoration: 'none', color: '#000' }} >Proceed</a>
        </button>
      )}
      </div>
  );
};

export default ProfilePage;
